import { Component } from 'react';


class Prueba extends Component {
    state = { 
        contador: 1
    }
    handleSize(){
        const size = 8
        let style = {
            fontSize: size,
            fontWeight: "Bold",
            color:"#FF0000"
        }
        return style
    }
    render() { 
        return (<p style = {this.handleSize()}>Esto es JSX {this.state.contador}</p>);
    }
}

export default Prueba;
